import React from 'react';
import styled from 'styled-components';
import QuestionnaireForm from '../components/questionnaire/QuestionnaireForm';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const PageContainer = styled.div`
  padding: 3rem 1rem;
  max-width: 1200px;
  margin: 0 auto;

  @media (min-width: 768px) {
    padding: 4rem 2rem;
  }
`;

const PageHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: var(--text-light);
`;

const Subtitle = styled.p`
  color: var(--text-dim);
  font-size: 1.2rem;
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const InfoBox = styled.div`
  background: rgba(0, 112, 240, 0.1);
  border-left: 4px solid var(--primary-color);
  padding: 1.5rem;
  border-radius: 8px;
  margin-bottom: 2rem;

  p {
    margin-bottom: 0.5rem;
    color: var(--text-dim);
  }

  ul {
    margin-left: 1.5rem;
    margin-top: 0.5rem;
  }

  li {
    margin-bottom: 0.5rem;
    color: var(--text-dim);
  }
`;

const LoginPrompt = styled.div`
  text-align: center;
  background: rgba(255, 255, 255, 0.05);
  padding: 2rem;
  border-radius: 16px;
  max-width: 500px;
  margin: 0 auto;
  backdrop-filter: blur(10px);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const LoginTitle = styled.h2`
  margin-bottom: 1rem;
  font-size: 1.5rem;
`;

const LoginButton = styled(Link)`
  display: inline-block;
  background: var(--primary-color);
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  text-decoration: none;
  margin-top: 1rem;
  transition: all 0.3s ease;

  &:hover {
    background: var(--secondary-color);
    transform: translateY(-3px);
    text-decoration: none;
  }
`;

const QuestionnairePage = () => {
  const { currentUser } = useAuth();

  return (
    <PageContainer>
      <PageHeader>
        <Title>Financial Questionnaire</Title>
        <Subtitle>
          Fill out this questionnaire to create your personalized financial dam visualization
          and explore scenarios to achieve your dream life.
        </Subtitle>
      </PageHeader>

      <InfoBox>
        <p><strong>Why we need this information:</strong></p>
        <p>This data helps us create a detailed visualization of your financial situation and provide meaningful insights.</p>
        <p><strong>What you'll need to provide:</strong></p>
        <ul>
          <li>Basic personal information</li>
          <li>Monthly income and expenses</li>
          <li>Assets and liabilities</li>
          <li>Financial goals</li>
          <li>A description of your dream life</li>
        </ul>
        <p><strong>Privacy:</strong> Your data is encrypted and securely stored. We never share your information with third parties.</p>
      </InfoBox>

      {currentUser ? (
        <QuestionnaireForm />
      ) : (
        <LoginPrompt>
          <LoginTitle>Please Sign In</LoginTitle>
          <p>You need to create an account or sign in to save your financial data and access the dashboard.</p>
          <LoginButton to="/">Sign In / Create Account</LoginButton>
        </LoginPrompt>
      )}
    </PageContainer>
  );
};

export default QuestionnairePage;
